对于 Grpc 项目，做法也是大同小异。
创建一个 BusinessInterceptor 异常拦截器。
/// 
/// 业务异常拦截器.
/// 
public class BusinessInterceptor : Interceptor
{
    private readonly ILogger _logger;
    private readonly IStringLocalizer _stringLocalizer;

    /// 
    /// Initializes a new instance of the  class.
    /// 
    /// 
    /// 
    public BusinessInterceptor(ILogger logger, IStringLocalizer stringLocalizer)
    {
        _logger = logger;
        _stringLocalizer = stringLocalizer;
    }

    /// 
    public override async Task UnaryServerHandler(TRequest request, ServerCallContext context, UnaryServerMethod continuation)
        where TRequest : class
        where TResponse : class
    {
        try
        {
            var response = await continuation(request, context);
            return response;
        }
        catch (BusinessException ex)
        {
            // ... 打印日志 ...

            string message = string.Empty;
            if (ex.Paramters != null)
            {
                message = _stringLocalizer[ex.Message, ex.Paramters];
            }
            else
            {
                message = _stringLocalizer[ex.Message];
            }

            throw new RpcException(new Status(StatusCode.Internal, message));
        }
        catch (Exception ex)
        {
            // ... 打印日志 ...

            if (ex is RpcException)
            {
                throw;
            }

            throw new RpcException(new Status(StatusCode.Internal, ex.Message));
        }
    }
}

